  <validar_adjunto type="javascript" meta=""><![CDATA[function validar_adjunto(event){
  
  var input = event.target;  
  var reader = new FileReader();
  reader.onload = function(){
    var arrayBuffer = reader.result;
    var tamano = arrayBuffer.byteLength;
    if(tamano > 3000000){
      alert("La hoja de vida debe tener máximo 3 MB. El archivo seleccionado tiene "+(tamano/1048576).toFixed(2)+" MB.");
      getField("UP_HV").value = "";
    }    
  };
  reader.readAsArrayBuffer(input.files[0]);
}
getField("UP_HV").onchange=validar_adjunto;]]></validar_adjunto>
