ocultarFlechas();]]></ocultarFlechas><js_val type="javascript" meta=""><![CDATA[getField('BTN_ENVIAR').onclick = function() {
  
  var zona            = getField("DRP_ZONA").selectedIndex;
  var nom              = getField("TXT_NOM_EVE").value;
  var des             = getField("TXT_DES_EVE").value;
  var can             = getField("TXT_NUM_PAR").value;  
  var simbolos_text   = /^([áéíúóñÑa-zA-Z0-9\.\-\#\(\)\¿\?\+\-\=\;\,\:\@\!\¡\*\/\%\$\s]+)$/;
  
  if(zona == 0){
    alert("Seleccione una zona para realizar la reserva.");
      return false;
  }
  
  if(!simbolos_text.test(nom)){
    alert("El nombre del evento debe tener solo letras, números, y los siguientes simbolos: ., -, #, (, ), ¿, ?, +, -, =, ;, ,, :, @, !, ¡, *, /, %, $.");
    return false;
  }

  if(!simbolos_text.test(des)){
    alert("La descripción del evento debe tener solo letras, números, y los siguientes simbolos: ., -, #, (, ), ¿, ?, +, -, =, ;, ,, :, @, !, ¡, *, /, %, $.");
    return false;
  }

  if(isNaN(can) || can == '0' || can == ""){
    alert("La cantidad de participantes debe ser númerico y mayor a cero.");
    return false;
  }

  var cantidad = Number_Rows_Grid("GRD_EVE_MUL2_1", "DATE_EVE_MUL");

  for(var i=1; i <= cantidad; i++){
    var hi              = getGridField("GRD_EVE_MUL2_1",i,"DRP_HORA_INI_EVE_MUL").selectedIndex;
    var hin             = getGridField("GRD_EVE_MUL2_1",i,"DRP_HORA_INI_EVE_MUL").options[hi].text;
    var hf              = getGridField("GRD_EVE_MUL2_1",i,"DRP_HORA_FIN_EVE_MUL").selectedIndex;
    var hfn             = getGridField("GRD_EVE_MUL2_1",i,"DRP_HORA_FIN_EVE_MUL").options[hf].text;

    var arHora1 = hin.split(":"); 
    var arHora2 = hfn.split(":"); 
     
    // Obtener horas y minutos (hora 1) 
    var hh1   = parseInt(arHora1[0],10); 
    var mm1   = parseInt(arHora1[1],10); 

    // Obtener horas y minutos (hora 2) 
    var hh2   = parseInt(arHora2[0],10); 
    var mm2   = parseInt(arHora2[1],10);

    // Comparar 
    if (hh2<hh1 || (hh1==hh2 && mm2<mm1) || (hh1==hh2 && mm2==mm1)){
      alert("La hora de fin del evento debe ser mayor a la hora de inicio.");
      return false;
    }
  }
  return true; 
}]]></js_val></dynaForm>
