  <GRD_BORRAR_EVE type="grid" xmlgrid="506113380552be1f0eacde3033093422/486370514555b889d8f70e4018234989" addrow="0" deleterow="0" required="0" readonly="0" optgroup="0"/>
  <DRP_SEL type="dropdown" required="0" readonly="0" optgroup="0" mode="edit">
    <eN>SELECCIONE UNA OPCIÓN      
      <option name="0">BORRAR TODO EL EVENTO</option>
      <option name="1">BORRAR HORARIO DENTRO DE UN EVENTO</option>
      <option name="2">NO ELIMINAR NINGUNO </option>
      <option name="3">USUARIO SIN SOLICITUDES</option></eN>
  </DRP_SEL>
  <BTN_ENVIAR type="submit" required="0" readonly="0" optgroup="0">
    <eN>CONTINUAR</eN>
    <es>BUSCAR EVENTOS</es>
  </BTN_ENVIAR>
  <validar_formulario type="javascript" meta=""><![CDATA[getField('BTN_ENVIAR').onclick = function() {
  
  
  var cant = Number_Rows_Grid("GRD_BORRAR_EVE", "TXT_CASO");
  var opc  = getField("DRP_SEL").value;
  var sen  = 0;
  
  if(opc == 2){
    return true;
  }

  if(cant == 1 && getGridField("GRD_BORRAR_EVE",1,"TXT_CASO").value == "" && opc != 3){
    alert("El usuario no tiene solicitudes registradas.");
    return false;
  }
  
  if(cant == 1 && getGridField("GRD_BORRAR_EVE",1,"TXT_CASO").value == "" && opc == 3){
    return true;
  }

  for(var i=1; i<=cant; i++){
    
    if(getGridField("GRD_BORRAR_EVE",i,"CHK_OPCION").checked == true){
      sen++;      
    }    
  }

  if(sen == 0){
    alert("Debe seleccionar un evento a eliminar.");
    return false;
  }
  else if(sen != 1){
    alert("Solo debe seleccionar un elemento a la vez.");
    return false;
  }
  
  if(opc == 0){
    
    var res = confirm("El evento puede tener varias fechas asignadas, ¿Está seguro de eliminarlo?");
    if (res == true) {
      return true;
    } else {
      return false;
    }
  }

  return true;

}]]></validar_formulario>
</dynaForm>
