  var cantidad = Number_Rows_Grid("GRD_NEW_EVE_DEV", "DATE_EVE_MUL");

  for(var i=1; i <= cantidad; i++){
    var hi              = getGridField("GRD_NEW_EVE_DEV",i,"DRP_HORA_INI_EVE_MUL").selectedIndex;
    var hin             = getGridField("GRD_NEW_EVE_DEV",i,"DRP_HORA_INI_EVE_MUL").options[hi].text;
    var hf              = getGridField("GRD_NEW_EVE_DEV",i,"DRP_HORA_FIN_EVE_MUL").selectedIndex;
    var hfn             = getGridField("GRD_NEW_EVE_DEV",i,"DRP_HORA_FIN_EVE_MUL").options[hf].text;

    var arHora1 = hin.split(":"); 
    var arHora2 = hfn.split(":"); 
     
    // Obtener horas y minutos (hora 1) 
    var hh1   = parseInt(arHora1[0],10); 
    var mm1   = parseInt(arHora1[1],10); 

    // Obtener horas y minutos (hora 2) 
    var hh2   = parseInt(arHora2[0],10); 
    var mm2   = parseInt(arHora2[1],10);

    // Comparar 
    if (hh2<hh1 || (hh1==hh2 && mm2<mm1) || (hh1==hh2 && mm2==mm1)){
      alert("La hora de fin del evento debe ser mayor a la hora de inicio.");
      return false;
    }
  }
  return true; 
}]]></JS_nota></dynaForm>00000003290899493855522f0024a704037410901000004707<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">
