  <VALIDAR_FILAS type="javascript" meta=""><![CDATA[//En el siguiente código se agregan los libros a la grilla
getField('BTN_ANADIR').onclick = function() {
  var id_usu= getField('TXT_NUM_DOC').value;
  var id_lib = getField('TXT_ID_LIB').value;
  var est_lib = getField('TXT_EST_PRES').value;
  var estado = getField('TXT_ESTADO_REG').value;
  var cantidad= getField('TXT_CANTIDAD').value;
  
  
  if(estado == "SUSPENDIDO"){
    alert("El usuario se encuentra suspendido para el prestamo de libros, debe esperar a que termine su tiempo de suspensión para solicitar libros en prestamo.");
    return false;
  }
  if(estado == "SIN VALIDAR"){
    alert("La cuenta del usuario aun no ha sido validada. No puede solicitar prestamo de libros hasta que la cuenta haya sido validada.");
    return false;
  }
  if(id_usu == ""){
    alert("Por favor, ingrese un usuario para realizar la solicitud de prestamo.");
    return false;
  }
  if(id_lib == ""){
    alert("Por favor, seleccione un libro para adicionar a la solicitud de prestamo.");
    return false;
  }
  if(getGridField("GRL_PRES_LIB",1,"TXT_NOM_USU").value != ""){
    var suma = parseInt(cantidad) + Number_Rows_Grid("GRL_PRES_LIB", "TXT_NOM_USU");
  if(suma >= 3){      
      alert("Cada usuario puede prestar máximo 3 libros en total." );
      return false;
  }
}
if(getGridField("GRL_PRES_LIB",1,"TXT_NOM_USU").value == ""){
  if(cantidad >= 3){
      alert("Cada usuario puede prestar máximo 3 libros en total." );
      return false;
  }
}
  
  
  if(est_lib == "NO DISPONIBLE"){
    alert("Este libro no se encuentra disponible para prestamo.");
    return false;
  }
  if (Number_Rows_Grid("GRL_PRES_LIB", "TXT_NOM_USU") < 3){
    
    var doc     = getField('TXT_NUM_DOC').value;
    var usuario = getField('TXT_NOM_APE').value;
    var cod_lib = getField('TXT_ID_LIB').value;
    var tit_lib = getField('TXT_TIT_LIB').value;
    var f = new Date();
    var fecha = f.getFullYear() + "-" + (f.getMonth() +1) + "-" + f.getDate();
    tiempo=f.getTime();
    milisegundos=parseInt(15*24*60*60*1000);
    total=f.setTime(tiempo+milisegundos);
    var fecha_max = f.getFullYear() + "-" + (f.getMonth() +1) + "-" + f.getDate();
    
    for(i=1; i<=Number_Rows_Grid("GRL_PRES_LIB", "TXT_NOM_USU"); i++){
      
      if(cod_lib == getGridField("GRL_PRES_LIB",i,"ID_LIB").value){
        alert("El libro ya esta en la lista. No puede seleccionar el mismo libro mas de una vez.");
        return false;
      }
    }
    
    if(getGridField("GRL_PRES_LIB",1,"TXT_NOM_USU").value != ""){
      for(i=1; i<=Number_Rows_Grid("GRL_PRES_LIB", "TXT_NOM_USU"); i++){
      
        if(doc != getGridField("GRL_PRES_LIB",i,"TXT_DOC_USU").value){
           alert("La solicitud de prestamo de libros se debe realizar individualmente. No puede realizar una solicitud con usuarios diferentes.");
           return false;
          }
      }
    }
    
    if(Number_Rows_Grid("GRL_PRES_LIB", "TXT_NOM_USU") == 1 && getGridField("GRL_PRES_LIB",1,"TXT_NOM_USU").value == ""){
      getGridField("GRL_PRES_LIB",1,"TXT_DOC_USU").value = doc;
      getGridField("GRL_PRES_LIB",1,"TXT_NOM_USU").value = usuario;
      getGridField("GRL_PRES_LIB",1,"ID_LIB").value = cod_lib;
      getGridField("GRL_PRES_LIB",1,"TIT_LIB").value = tit_lib;
      getGridField("GRL_PRES_LIB",1,"DATE_PRESTAMO").value = fecha;
      getGridField("GRL_PRES_LIB",1,"DATE_MAX_PRESTAMO").value = fecha_max;
      
      getField('TXT_ID_LIB').value = "";
      getField('TXT_TIT_LIB').value = "";
      getField('TXT_AUT_LIB').value = "";
      getField('TXT_COD_LIB').value = "";
      getField('TXT_EST_LIB').value = "";
      getField('TXT_EST_PRES').value = "";
    }
    else if(Number_Rows_Grid("GRL_PRES_LIB", "TXT_NOM_USU") == 1 && getGridField("GRL_PRES_LIB",1,"TXT_NOM_USU").value != ""){
      getObject("GRL_PRES_LIB").addGridRow();
      getGridField("GRL_PRES_LIB",2,"TXT_DOC_USU").value = doc;
      getGridField("GRL_PRES_LIB",2,"TXT_NOM_USU").value = usuario;
      getGridField("GRL_PRES_LIB",2,"ID_LIB").value = cod_lib;
      getGridField("GRL_PRES_LIB",2,"TIT_LIB").value = tit_lib;
      getGridField("GRL_PRES_LIB",2,"DATE_PRESTAMO").value = fecha;
      getGridField("GRL_PRES_LIB",2,"DATE_MAX_PRESTAMO").value = fecha_max;
      
      getField('TXT_ID_LIB').value = "";
      getField('TXT_TIT_LIB').value = "";
      getField('TXT_AUT_LIB').value = "";
      getField('TXT_COD_LIB').value = "";
      getField('TXT_EST_LIB').value = "";
      getField('TXT_EST_PRES').value = "";
    }
    else if(Number_Rows_Grid("GRL_PRES_LIB", "TXT_NOM_USU") == 2){
      getObject("GRL_PRES_LIB").addGridRow();
      getGridField("GRL_PRES_LIB",3,"TXT_DOC_USU").value = doc;
      getGridField("GRL_PRES_LIB",3,"TXT_NOM_USU").value = usuario;
      getGridField("GRL_PRES_LIB",3,"ID_LIB").value = cod_lib;
      getGridField("GRL_PRES_LIB",3,"TIT_LIB").value = tit_lib;
      getGridField("GRL_PRES_LIB",3,"DATE_PRESTAMO").value = fecha;
      getGridField("GRL_PRES_LIB",3,"DATE_MAX_PRESTAMO").value = fecha_max;
      
      getField('TXT_ID_LIB').value = "";
      getField('TXT_TIT_LIB').value = "";
      getField('TXT_AUT_LIB').value = "";
      getField('TXT_COD_LIB').value = "";
      getField('TXT_EST_LIB').value = "";
      getField('TXT_EST_PRES').value = "";
    }
    return true;
  }
  else{
    alert("No es permitido prestar mas de 3 libros por usuario.");
    return false;
  }
  
}
  
//En el siguiente código se valida si el nombre de usuario ya existe
  
getField("BTN_ENVIAR").form.onsubmit = function(){
  var doc = getGridField("GRL_PRES_LIB",1,"TXT_DOC_USU").value;
    
