  <valida_checkbox type="javascript" meta=""><![CDATA[getField("BTN_ENVIAR").form.onsubmit = function(){

  var cant = Number_Rows_Grid("GRL_DEV_LIB", "TXT_NOM_USU");

  for(var i=1; i<=cant; i++){
    
    if(getGridField("GRL_DEV_LIB",i,"CHK_DEV").checked == true && (getGridField("GRL_DEV_LIB",i,"CHK_REN").checked == true || getGridField("GRL_DEV_LIB",i,"CHK_PER").checked == true)){
      alert("Solo puede seleccionar una opcion por libro: devolver, renovar ó perdida.");
      return false;
    }

    if(getGridField("GRL_DEV_LIB",i,"CHK_REN").checked == true && (getGridField("GRL_DEV_LIB",i,"CHK_DEV").checked == true || getGridField("GRL_DEV_LIB",i,"CHK_PER").checked == true)){
      alert("Solo puede seleccionar una opcion por libro: devolver, renovar ó perdida.");
      return false;
    }

    if(getGridField("GRL_DEV_LIB",i,"CHK_PER").checked == true && (getGridField("GRL_DEV_LIB",i,"CHK_DEV").checked == true || getGridField("GRL_DEV_LIB",i,"CHK_REN").checked == true)){
      alert("Solo puede seleccionar una opcion por libro: devolver, renovar ó perdida.");
      return false;
    }
  }  
  
  var c = 0;
  for(var i=1; i<=cant; i++){
    if(getGridField("GRL_DEV_LIB",i,"CHK_PER").checked == true || getGridField("GRL_DEV_LIB",i,"CHK_DEV").checked == true || getGridField("GRL_DEV_LIB",i,"CHK_REN").checked == true){
      c++;
    }

  }

  if(c == 0){
    alert("Debe seleccionar al menos una opción para cada uno de los libros");
    return false;
  }

  return true;
}
  
