  <BTN_ENVIAR type="submit" required="0" readonly="0" optgroup="0">
    <es>ENVIAR</es>
  </BTN_ENVIAR>
  <JS_VAL_ENVIAR type="javascript" meta=""><![CDATA[getField("BTN_ENVIAR").form.onsubmit = function(){ 

  var sol = getField("DRP_TIPO_SOL").selectedIndex;
  var detalle = getField("TXT_OBSERVACION").value;
  
  
  if(sol==0){
  
    alert("Por favor seleccione una opcion");
      return false;
  }
   
  if(sol===1){
  
  if(detalle.length < 20 || detalle.length > 3000){
        alert("El detalle debe tener mínimo 20 caracteres y máximo 3000, su detalle tiene "+detalle.length+" caracteres");
        return false;
    }
  }
  
  if(sol===2){
   
    var con = Number_Rows_Grid("GRD_MATERIAL", "TXT_TIT_REC");
    for(var i=1; i<=con; i++)
    {
    
      var aux = getGridField("GRD_MATERIAL",i,"TXT_TIT_REC").value
          if(aux==""){
          
            alert("El titulo del recurso solicitado no puede ser vacio");
            return false;
          }
    
    }
  
  
  }
  

}
  
  
  
function ocultarFlechas(){
  hidden(getField("DYN_BACKWARD").parentNode);
  hidden(getField("DYN_FORWARD").parentNode);
}
ocultarFlechas();]]></JS_VAL_ENVIAR>
