  var ocuref       = getField("TXT_OCU_REF_REG").value;
  var simbolos_text = /^([√°√©√≠√∫√≥√±√ëa-zA-Z0-9\.\-\#\(\)\¬ø\?\+\-\=\;\,\:\@\!\¬°\*\/\%\$\s])/;
  var simbolos_alfanumerico = /^([√°√©√≠√∫√≥√±√ëa-zA-Z0-9])/;
